mapper.insert:
insert into article_tag(
    creation,
    lastmod,
    "version",
    article,
    tag
) values(
    #{creationDate},
    #{lastModifiedDate},
    #{version},
    #{articleId},
    #{tagId}
);

mapper.update:
update article_tag
creation = #{creationDate}, lastmod = #{lastModifiedDate}, "version" = #{version}, article = #{articleId}, tag = #{tagId},
id = #{id}
