mapper.insert:
insert into article_tag(
    creation,
    lastmod,
    "version",
    article,
    tag
) values(
    #{creationDate},
    #{lastModified},
    #{version},
    #{articleId},
    #{tagId}
);

mapper.update:
update article_tag
creation = #{creationDate}, lastmod = #{lastModified}, "version" = #{version}, article = #{articleId}, tag = #{tagId},
id = #{id}
