mapper.insert:
insert into post_tag(
    creation,
    lastmod,
    "version",
    post,
    tag
) values(
    #{creationDate},
    #{lastModifiedDate},
    #{version},
    #{postId},
    #{tagId}
);

mapper.update:
update post_tag
creation = #{creationDate}, lastmod = #{lastModifiedDate}, "version" = #{version}, post = #{postId}, tag = #{tagId},
id = #{id}
