mapper.insert:
insert into post_tag(
    creation,
    lastmod,
    "version",
    post,
    tag
) values(
    #{creationDate},
    #{lastModified},
    #{version},
    #{postId},
    #{tagId}
);

mapper.update:
update post_tag
creation = #{creationDate}, lastmod = #{lastModified}, "version" = #{version}, post = #{postId}, tag = #{tagId},
id = #{id}
