mapper.insert:
insert into user_badge(
    creation,
    lastmod,
    "version",
    "user",
    badge
) values(
    #{creationDate},
    #{lastModified},
    #{version},
    #{userId},
    #{badgeId}
);

mapper.update:
update user_badge
creation = #{creationDate}, lastmod = #{lastModified}, "version" = #{version}, "user" = #{userId}, badge = #{badgeId},
id = #{id}
