mapper.insert:
insert into fabproc_sn(
    creation,
    lastmod,
    "version",
    proc,
    serial
) values(
    #{creationDate},
    #{lastModified},
    #{version},
    #{processId},
    #{serial}
);

mapper.update:
update fabproc_sn
creation = #{creationDate}, lastmod = #{lastModified}, "version" = #{version}, proc = #{processId}, serial = #{serial},
id = #{id}
