    org,
    ou,
    person,
    "role",
    description
) values(
    #{orgId},
    #{ouId},
    #{personId},
    #{role},
    #{description}
);

mapper.update:
update personrole
org = #{orgId}, ou = #{ouId}, person = #{personId}, "role" = #{role}, description = #{description},
id = #{id}
