mapper.insert:
insert into cartitem(
    label, description,
    priority, flags, state, uid, gid, mode, acl,
    shopitem, price, qty
) values(
    #{label}, #{description},
    #{priority}, #{flags}, #{state}, #{ownerUser.id}, #{ownerGroup.id}, #{accessMode}, #{acl},
    #{shopItem.id}, #{price}, #{quantity}
);

mapper.update:
update cartitem
shopitem = #{shopItem.id}, price = #{price}, qty = #{quantity},
id = #{id}
